package com.zcy.cn.controller;import com.zcy.cn.entity.UserInfo;import com.zcy.cn.service.UserService;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.web.bind.annotation.*;import javax.jws.soap.SOAPBinding;/** * @author 朱朝阳 * @date 2019/12/26 18:15 */@RestController@RequestMapping("/userInfo")public class UserInfoController {    @Autowired    UserService userService;    @PostMapping("/save")    public UserInfo save(@RequestBody UserInfo userInfo) {        return userService.save(userInfo);    }    @GetMapping("/{id}")    public UserInfo find(@PathVariable("id") Long id) {        return userService.findById(id);    }}